import React,{Component} from 'react';
import './Tasks-Filter.css'


export default class TasksFilter extends Component {

   onSelect = (e) => {
  const text = e.target
  this.props.onSelectFilter(text);
}



render() {


    return(
      
        <ul className="filters">
          <li>
            <button    onClick = {this.onSelect}>All</button>
          </li>
          <li>
            <button  onClick = {this.onSelect}>Active</button>
          </li>
          <li>
            <button  onClick = {this.onSelect}>Completed</button>
          </li>
        </ul>
       

    );
};
}
