import React,{Component} from 'react';
import Footer from '../Footer/Footer';
import NewTaskForm from '../New-Task-Form/New-Task-Form';
import TaskList from '../Task-List/Task-List';

import './App.css'




export default class App extends Component {

    state  = {
         Data:[
        {id: 1,  description : "Completed task",created: "2024-01-01"},
        {id: 2,  description : "Editing task",created: "2023-02-01" },
        {id: 3,  description : "Active task",created: "2025-03-01"},
         ]
    };

onDestroy = (id) => { 
   this.setState (({Data}) => {
    const idx = Data.findIndex((el) => el.id === id) 
    const newArrayData = [
    ...Data.slice(0,idx),
    ...Data.slice(idx + 1)
      ]
   
return {
    Data : newArrayData
};
});

};



render() {
  const {Data} = this.state

return(
<div>
<section class="todoapp"> 
<NewTaskForm/>
<section class="main">
<TaskList Tasks = {Data}
onDestroy = {(id) => this.onDestroy(id)}/>
<Footer/>
</section>
</section>
</div>
);
};
};
