import React,{Component} from 'react';
import Footer from '../Footer/Footer';
import NewTaskForm from '../New-Task-Form/New-Task-Form';
import TaskList from '../Task-List/Task-List';

import './App.css'




export default class App extends Component {

    idItem = 10;
   

    state  = {
         Data:[
            this.craeteDataItem("Active task"),
             this.craeteDataItem("Active1 task"),
             this.craeteDataItem("Active2 task"),
         ]
    };
  
      craeteDataItem(label){
        return {
            id: this.idItem ++,  
            description : label,
            created: "2024-01-01", 
            done: true,
            edit: ""
        };
};
      

       addDataItem = (text) => {

       const newDataItem =  this.craeteDataItem(text);
         this.setState(({Data}) =>  {

    const newData = [...Data,newDataItem];

       return {
        Data : newData
       
           };
        });
      };

     


onDestroy = (id) => { 
   this.setState (({Data}) => {
    const idx = Data.findIndex((el) => el.id === id) 
    const newArrayData = [
    ...Data.slice(0,idx),
    ...Data.slice(idx + 1)
      ]
   
return {
    Data : newArrayData
};
});
};

onDoneClick = (id) => {
    this.setState (({Data}) => {
        const idx = Data.findIndex((el) => el.id === id) ;
  const oldIdData = Data[idx];
  const newIdData = {...oldIdData,done : ! oldIdData.done}
  const newArrayData = [
    ...Data.slice(0,idx),
    newIdData,
    ...Data.slice(idx + 1)
      ]
      return {
        Data : newArrayData
      };
});
};


render() {
  const {Data} = this.state;
  const countDoItem = this.state.Data.filter((el) => el.done).length;
  const toDoCounts = Data.length  - countDoItem;
  //console.lod({Data});

return(
<div>
<section class="todoapp"> 
<NewTaskForm/>
<section class="main">
<TaskList Tasks = {Data}
onDestroy = {(id) => this.onDestroy(id)}
onDoneClick = {(id) => this.onDoneClick(id)}
/>
<Footer toDoCounts = {countDoItem}/>
</section>
</section>
</div>
);
};
};
