import React,{Component} from 'react';
import Footer from '../Footer/Footer';
import NewTaskForm from '../New-Task-Form/New-Task-Form';
import TaskList from '../Task-List/Task-List';

import './App.css'




export default class App extends Component {

    idItem = 10;
   

    state  = {
         Data:[
            this.craeteDataItem("Active task"),
             this.craeteDataItem("Active1 task"),
             this.craeteDataItem("Active2 task"),
         ],
         filterKey: [{id: 1, type: "All", selected: true},
                      {id: 2,type: "Active", selected: false},
                      {id: 3,type: "Completed", selected: false},
         ]
    };
  
      craeteDataItem(label){
        return {
            id: this.idItem ++,  
            description : label,
            created: "2024-01-01", 
            done: false,
            edit: ""
        };
};
      

       addDataItem = (text) => {

       const newDataItem =  this.craeteDataItem(text);
         this.setState(({Data}) =>  {

    const newData = [...Data,newDataItem];

       return {
        Data : newData
       
           };
        });
      };

  onFillterChainge = (select)  => {

   
   const newFilterKey = this.state.filterKey.map((item) =>
    item.selected = false)

  const itemFilterKey= newFilterKey.find((el) => el.type === select)
  /* itemFilterKey.select = true
   const idx = itemFilterKey.id
   const newArray = [
     ...newFilterKey.slice(0,idx),
      itemFilterKey,
     ...newFilterKey.slice(idx + 1)
     ];*/
     console.log(itemFilterKey,select)
     
    }
   


      

onDestroy = (id) => { 
   this.setState (({Data}) => {
    const idx = Data.findIndex((el) => el.id === id) 
    const newArrayData = [
    ...Data.slice(0,idx),
    ...Data.slice(idx + 1)
      ]
   
return {
    Data : newArrayData
};
});
};

onDoneClick = (id) => {
    this.setState (({Data}) => {
        const idx = Data.findIndex((el) => el.id === id) ;
  const oldIdData = Data[idx];
  const newIdData = {...oldIdData,done : ! oldIdData.done}
  const newArrayData = [
    ...Data.slice(0,idx),
    newIdData,
    ...Data.slice(idx + 1)
      ];
       
      return {
        Data : newArrayData
      };
});
};


render() {


    const  {filterKey} = this.state;
     const filterKeySelect = this.state.filterKey.filter((el) => el.selected)
  const {Data} = this.state;
  let dataFilter = Data;
 if (filterKeySelect.type === "all") {  
    dataFilter = Data 
 } 
 if (filterKeySelect.type === "activ") {
    dataFilter = this.state.Data.filter((el) => el.done === false) 
    
 } 
  if (filterKeySelect.type === "completed"){
    dataFilter = this.state.Data.filter((el) => el.done)
 };
 
  const countDoItem = this.state.Data.filter((el) => el.done).length;
  const toDoCounts = this.state.Data.length  - countDoItem;
 
return(
<div>
<section class="todoapp"> 
<NewTaskForm  addDataItem = {(text) => this.addDataItem(text)}  />
<section class="main">
<TaskList Tasks = {dataFilter}
onDestroy = {(id) => this.onDestroy(id)}
onDoneClick = {(id) => this.onDoneClick(id)}
/>
<Footer toDoCounts = {toDoCounts}
onSelectFilter = {(select) => this.onFillterChainge(select)} 
filterKey = {filterKey}/>
</section>
</section>
</div>
);
};
};
