import React from 'react';
import Task from '../Task/Task';

const TaskList = (tasks) => {
  const Tasks = [
    
    {mode : "completed", description : "Completed task",created: "created 17 seconds ago"},
    {mode : "editing", description : "Editing task",created: "created 5 minutes ago" },
    {mode : "", description : "Active task",created: "created 1 minutes ago"},
    
    ];




  const TaskLists = Tasks.map((item)  => {
     
        const {mode, ...itemsProp } = item;
        const isEditing = false;
         if (mode == "editing" ){
            isEditing = true;
            };
   return(
    <li className = {mode}>
    <Task {...itemsProp}/>
   <input type="text" className="edit" value="Editing task"/>
    </li>
   );
  });


    return (
    
       <ul className="todo-list">
      {TaskLists}
      </ul>
    );
};
 export default TaskList  ;
