import React from 'react';
import './Tasks-Filter.css'


const TasksFilter = ({onSelectFilter}) => {

  const  onSelect = (e) => {
  const text = e.target
  onSelectFilter(text);



}



    return(
      
        <ul className="filters">
          <li>
            <button className="selected"  onClick = {onSelect}>All</button>
          </li>
          <li>
            <button onClick = {onSelect}>Active</button>
          </li>
          <li>
            <button onClick = {onSelect}>Completed</button>
          </li>
        </ul>
       

    );
};
 export default TasksFilter ;
