import React from 'react';


const ButtonFilter = ({type, selected, onSelect }) => {

   let clas = "";

   if (selected) {clas = "selected"}

    return(
      <li>
      <button class = { clas } onClick = {onSelect}>{type}</button>
    </li>

    );
};
 export default ButtonFilter ;
