import React from 'react';
import './Tasks-Filter.css'


const TasksFilter = ({onSelectFilter, selectKey }) => {

  const  onSelect = (e) => {
  const text = e.target
  onSelectFilter(text);
}
 let  {all, active, completed} = ""
 if (selectKey =="all") { 
     all = "selected"}


    return(
      
        <ul className="filters">
          <li>
            <button className = {all}   onClick = {onSelect}>All</button>
          </li>
          <li>
            <button className = {active} onClick = {onSelect}>Active</button>
          </li>
          <li>
            <button className = {completed} onClick = {onSelect}>Completed</button>
          </li>
        </ul>
       

    );
};
 export default TasksFilter ;
