import React,{Component} from 'react';
import formatDistanceToNow from 'date-fns/formatDistanceToNow'
import './Task.css'

export default class Task extends Component {
    
  
      
      state  = {
        done: false,
        edit: ""
      };
  
      onDoneClick = () => {
       this.setState({
        done: !this.state.done,
      
       });
      };
  
      

render()  {
  const {id, description,created} = this.props

    const dateDistance = formatDistanceToNow(new Date(created),
    {includeSeconds: true}) ;
    const {done,edit} = this.state;
    let className;
    (done) ? className = "completed": className = "";
    className +=  edit;

    return(
    <li key = {id} className = {className}
           >
       <div className="view">
    <input className="toggle" type="checkbox"onClick={this.onDoneClick} />
    <label>
      <span className="description">{description}</span>
      <span className="created">{dateDistance}</span>
    </label>
    <button className="icon icon-edit" ></button>
    <button className="icon icon-destroy" onClick = {this.props.onDestroy}  ></button>
  </div>
  <input type="text" className="edit" value="Editing task"/>
  </li>
 

    );
};
};
